import { Component } from '@angular/core';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    title = 'Názov projektu';
    ello = true;
    changeProjectTitle(title: string): void {
        this.title = title;
    }
    checkbox(ello: boolean): void {
        this.ello = ello
    }
}

<div class="header">
    <h1>{{ title }}</h1>
    <h2>{{ ello }}</h2>
</div>
<div>
    <label>Zadajte názov projektu:
        <input #projectTitle type="text" [value]="title"/>
        <input #cau type="checkbox" [value]="ello"/>
    </label>
</div>
<button (click)="changeProjectTitle(projectTitle.value)">Zmeniť názov</button>
<button (click)="checkbox(cau.checked)">Zmeniť názov</button>
