import { Component } from '@angular/core';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    number = Array(8).fill(0).map((x,i)=>i);

    cislo = [0];
    checkboard = [false, false, false, false, false, false, false, false];
    x = 0;

    checkbox(input: number): void {
        if(input > 255){
            alert("Zadane cislo moze byt maximalne 255")
            return
        }
        this.cislo = [];
        this.x = input;
        console.log(this.x)
        while (this.x > 0){
            console.log("this.x")
            this.cislo.push(this.x % 2)
            this.x = Math.floor(this.x/2)
        }
        this.checkboard=[]
        for( let x = 0; x < 8; x++){
            if(this.cislo[x] == 0){
                this.checkboard.push(false)
            }else if (this.cislo[x] == 1){
                this.checkboard.push(true)
            }else{
                this.checkboard.push(false)
            }
        }
    }

    pridaj(input: number){
        console.log(input)
    }
}
