import { Component } from '@angular/core';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    title = 'Názov projektu';
    changeProjectTitle(title: string): void {
        this.title = title;
    }
}

<div class="header">
    <h1>{{ title }}</h1>
</div>
<div>
    <label>Zadajte názov projektu:
        <input #projectTitle type="text" [value]="title"/>
    </label>
</div>
<button (click)="changeProjectTitle(projectTitle.value)">Zmeniť názov</button>
